import React, { useState } from "react";
import {
  Box,
  Text,
  HStack,
  Icon,
  VStack,
  AddIcon,
  Image,
  Center,
} from "@gluestack-ui/themed";
import {
  ScrollView,
  Dimensions,
  StatusBar,
  FlatList,
  ListRenderItemInfo,
} from "react-native";
import { Svg } from "react-native-svg";
import {
  MapPin,
  ChevronDown,
  Bell,
  Wallet,
  Plus,
  History,
  Store,
  BadgePercent,
  Users,
  Info,
  Link,
  Star,
  MessageSquare,
} from "lucide-react-native";

interface Item {
  id: string;
  name: string;
  address: string;
  star: string;
  message: string;
  distance: string;
}

export default function HomeScreen() {
  const { width, height } = Dimensions.get("window");
  const hijauZala = "#539D00";
  const biruZala = "#00A1FF";
  const donkerZala = "#003C60";
  const shadow = {
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.8,
    shadowRadius: 10,
    elevation: 5,
  };


  interface LaundryItem {
    id: string;
